# ----------------------------------------------------------------------------------------------------
# Name: htop
# Description: htop is an interactive process viewer and system monitor for Unix-like operating systems, providing a user-friendly interface to monitor system resources and manage processes.
# Author: reven
# Revision: 4
# Icon: data:image/svg+xml;base64,
# ----------------------------------------------------------------------------------------------------

#!/bin/bash

# Function to handle errors
function handle_error {
    echo "An error occurred. Exiting..."
    exit 1
}

# Check if the --uninstall option is provided
if [ "$1" == "--uninstall" ]; then
    echo "Uninstalling htop..."
    if apt-get remove -y htop; then
        echo "htop successfully uninstalled."
        exit 0
    else
        handle_error
    fi
fi

# Check if htop is installed
if command -v htop &>/dev/null; then
    echo "htop is already installed."
    exit 0
fi

echo "htop is not installed. Installing..."

# Update package lists and install htop
if ! apt-get -qq update || ! apt-get install -yqq htop; then
    handle_error
fi

echo "Installation complete."

# Verify installation
if command -v htop &>/dev/null; then
    echo "htop successfully installed."
    exit 0
fi

echo "Failed to install htop."
exit 1