# ----------------------------------------------------------------------------------------------------
# Name: Optimize CUDA Alpha
# Description: Installs the alpha of FFmpeg FileFlows Edition + CUDA
# Revision: 2
# Icon: data:image/png;base64,
# ----------------------------------------------------------------------------------------------------

#!/bin/bash

set -e

# --------------- Configurable Paths --------------------
# Constants
FFMPEG_DIR="/usr/lib/ffmpeg-fileflows-edition"
FFMPEG_LINK=/usr/local/bin/ffmpeg
FFMPEG_VERSION=7.1.2-alpha1
FFMPEG_TARGET="noble"

# ---------------------- Utility ------------------------
function handle_error {
    echo "An error occurred. Exiting..."
    exit 1
}

# ---------------------- FFmpeg ------------------------
function is_ffmpeg_installed {
    [ -x "$FFMPEG_LINK" ] && "$FFMPEG_LINK" -version 2>/dev/null | grep -q FileFlows && dpkg-query -W ffmpeg-fileflows-edition | grep -q "$FFMPEG_VERSION"
}

function install_ffmpeg {
    if is_ffmpeg_installed; then
        echo "FFmpeg FileFlows Edition is already installed. ($FFMPEG_DIR)"
        return
    fi

    echo "Installing FFmpeg FileFlows Edition..."

    architecture=$(uname -m)
    if [[ ! "$architecture" =~ ^(x86_64|aarch64|armv7l)$ ]]; then
        echo "Unsupported architecture: $architecture"
        exit 1
    elif [[ "$architecture" == aarch64 ]]; then
        TARGET_ARCH="arm64"
    else
        TARGET_ARCH="amd64"
    fi

    apt-get -qq update

    wget --no-verbose -O /tmp/ffmpeg-fileflows-edition.deb "https://github.com/lawrencecurtis/ffmpeg-fileflows-edition/releases/download/v${FFMPEG_VERSION}/ffmpeg-fileflows-edition_${FFMPEG_VERSION}-${FFMPEG_TARGET}_${TARGET_ARCH}.deb"
    apt-get install --no-install-recommends --no-install-suggests -yqq intel-opencl-icd /tmp/ffmpeg-fileflows-edition.deb
    rm /tmp/ffmpeg-fileflows-edition.deb

    ln -sf "$FFMPEG_DIR/ffmpeg" /usr/local/bin/ffmpeg
    ln -sf "$FFMPEG_DIR/ffprobe" /usr/local/bin/ffprobe
    ln -sf "$FFMPEG_DIR/dovi_tool" /usr/local/bin/dovi_tool

    echo "FFmpeg FileFlows Edition installed. ($FFMPEG_DIR)"
    echo "‚úÖ All components installed."
}

function uninstall_ffmpeg {
    echo "Uninstalling FFmpeg FileFlows Edition..."
    rm -f /usr/local/bin/ffmpeg /usr/local/bin/ffprobe /usr/local/bin/dovi_tool
    apt-get remove --purge -y ffmpeg-fileflows-edition || echo "Not installed."
    echo "üóëÔ∏è All components uninstalled."
}

# ---------------------- Entrypoint ------------------------
if [ "$1" == "--uninstall" ]; then
    uninstall_ffmpeg
else
    install_ffmpeg
fi

exit 0
